package textadventure;import items.Container;/** * A Player can contain objects and knows the Room it's currently in. */public class Player extends Container {	/** Room this Player is in at the moment */	private Room currentRoom;	private int health;	private boolean hasBrushedTeeth;	private boolean isDressed;		/** Creates a player that starts in the given Room with no items */	public Player(Room startRoom, World world) {		super(world, "Player", "You are looking good today!");		currentRoom = startRoom;		isDressed = false;		hasBrushedTeeth = false;		health = 0;	}	/* Getters and setters */	public Room getCurrentRoom() {		return currentRoom;	}	public void setCurrentRoom(Room newRoom) {		currentRoom = newRoom;	}	public int getHealth() {		return health;	}	public void setHealth(int health) {		this.health = health;	}	public boolean hasBrushedTeeth() {		return hasBrushedTeeth;	}	public void setBrushedTeeth(boolean hasBrushedTeeth) {		this.hasBrushedTeeth = hasBrushedTeeth;	}	public boolean isDressed() {		return isDressed;	}	public void setDressed(boolean isDressed) {		this.isDressed = isDressed;	}}